INSERT INTO `wpim_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2012-12-11 17:21:08', '2012-12-11 17:21:08', 'Bem-vindo ao WordPress. Esse é o seu primeiro post. Edite-o ou exclua-o, e então comece a publicar!', 'Olá, mundo!', '', 'publish', 'open', 'open', '', 'ola-mundo', '', '', '2012-12-11 17:21:08', '2012-12-11 17:21:08', '', 0, 'http://localhost/imagemama/site/?p=1', 0, 'post', '', 1),
(2, 1, '2012-12-11 17:21:08', '2012-12-11 17:21:08', 'Este é o exemplo de uma página. É diferente de um post de blog porque é estática e pode aparecer em menus de navegação (na maioria dos temas). A maioria das pessoas começam com uma página ''Sobre'' que as apresenta aos potenciais visitantes do site. Você pode usar algo como:\n\n<blockquote>Oi! Sou um estudante de Biologia e gosto de esportes e natureza. Nos fins-de-semana pratico futebol com meus amigos no clube local. Eu moro em Valinhos e fiz este site para falar sobre minha cidade.</blockquote>\n\n...ou algo como:\n\n<blockquote>A empresa Logos foi fundada em 1980, e tem provido o comércio local com o que há de melhor em informatização. Localizada em Recife, nossa empresa tem se destacado como um das que também contribuem para o descarte correto de equipamentos eletrônicos substituídos.</blockquote>\n\nComo um novo usuário WordPress, vá ao seu <a href="http://localhost/imagemama/site/wp-admin/">Painel</a> para excluir este conteúdo e criar o seu. Divirta-se!', 'Página de Exemplo', '', 'trash', 'open', 'open', '', 'pagina-exemplo', '', '', '2012-12-12 22:32:01', '2012-12-12 22:32:01', '', 0, 'http://localhost/imagemama/site/?page_id=2', 0, 'page', '', 0),
(3, 1, '2012-12-11 17:21:23', '0000-00-00 00:00:00', '', 'Rascunho automático', '', 'auto-draft', 'open', 'open', '', '', '', '', '2012-12-11 17:21:23', '0000-00-00 00:00:00', '', 0, 'http://localhost/imagemama/site/?p=3', 0, 'post', '', 0),
